import React, { Component } from "react";
import { DataTable, Pagination } from "carbon-components-react";

const {
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableBody,
  TableCell,
  TableHeader,
  TableToolbar,
  TableToolbarContent,
  TableSelectRow,
  TableSelectAll,
  TableToolbarSearch,
  TableBatchActions,
  TableBatchAction
} = DataTable;

const plans = [
  {
    id: "a",
    name: "Load Balancer 0",
    protocol: "HTTP",
    port: 3000,
    rule: "Round robin",
    attached_groups: "Kevins VM Groups",
    status: "Disabled"
  },
  {
    id: "b",
    name: "Load Balancer 1",
    protocol: "HTTP",
    port: 443,
    rule: "Round robin",
    attached_groups: "Maureens VM Groups",
    status: "Starting"
  },
  {
    id: "c",
    name: "Load Balancer 2",
    protocol: "HTTP",
    port: 80,
    rule: "DNS delegation",
    attached_groups: "Andrews VM Groups",
    status: "Active"
  },
  {
    id: "d",
    name: "Load Balancer 3",
    protocol: "HTTP",
    port: 8023,
    rule: "DNS delegation",
    attached_groups: "Rameshs VM Groups",
    status: "Active"
  },
  {
    id: "e",
    name: "Load Balancer 4",
    protocol: "HTTP",
    port: 3533,
    rule: "DNS delegation",
    attached_groups: "Onkars VM Groups",
    status: "Active"
  },
  {
    id: "f",
    name: "Load Balancer 5",
    protocol: "HTTP",
    port: 4534,
    rule: "DNS delegation",
    attached_groups: "Vijays VM Groups",
    status: "Active"
  },
  {
    id: "g",
    name: "Load Balancer 6",
    protocol: "HTTP",
    port: 6787,
    rule: "DNS delegation",
    attached_groups: "Andrews VM Groups",
    status: "Active"
  },
  {
    id: "h",
    name: "Load Balancer 7",
    protocol: "HTTP",
    port: 3463,
    rule: "DNS delegation",
    attached_groups: "Andrews VM Groups",
    status: "Active"
  }
];

const headers = [
  {
    key: "name",
    header: "Name"
  },
  {
    key: "protocol",
    header: "Protocol"
  },
  {
    key: "port",
    header: "Port"
  },
  {
    key: "rule",
    header: "Rule"
  },
  {
    key: "attached_groups",
    header: "Attached Groups"
  },
  {
    key: "status",
    header: "Status"
  }
];

class HMCTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      plans: plans,
      startRow: 0,
      endRow: 3
    };
  }

  handlePageChange(e) {
    const startRow = e.pageSize * (e.page - 1);
    //console.log('startRow: ' + startRow);

    const endRow = e.pageSize * e.page;
    //console.log('endRow: ' + endRow);

    this.setState({ startRow: startRow, endRow: endRow });
  }

  handleOnInputValueChange = event => {
    if (event.target.value) {
      let filteredPlans = plans.filter(obj => {
        let match = false;
        Object.values(obj).forEach(value => {
          if (!match) {
            if (
              value
                .toString()
                .toLowerCase()
                .includes(event.target.value.toLowerCase())
            )
              match = true;
          }
        });

        return match;
      });

      console.log("filtered plans size", filteredPlans.length);
      this.setState({ plans: filteredPlans });
    } else {
      this.setState({ plans });
    }
  };

  render() {
    console.log("plans size", this.state.plans.length);
    return (
      <div>
        <DataTable
          rows={this.state.plans.slice(this.state.startRow, this.state.endRow)}
          headers={headers}
          render={({
            rows,
            headers,
            getHeaderProps,
            getRowProps,
            getSelectionProps,
            getBatchActionProps,
            selectedRows,
            onInputChange,
            selectRow
          }) => {
            return (
              <TableContainer title="DataTable">
                <TableToolbar>
                  <TableBatchActions {...getBatchActionProps()} />
                  <TableToolbarSearch
                    onChange={this.handleOnInputValueChange}
                  />
                </TableToolbar>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableSelectAll {...getSelectionProps()} />
                      {headers.map(header => (
                        <TableHeader {...getHeaderProps({ header })}>
                          {header.header}
                        </TableHeader>
                      ))}
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {rows.map(row => (
                      <TableRow key={row.id}>
                        <TableSelectRow {...getSelectionProps({ row })} />
                        {row.cells.map(cell => (
                          <TableCell key={cell.id}>{cell.value}</TableCell>
                        ))}
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
                <Pagination
                  totalItems={this.state.plans.length}
                  pageSize={3}
                  pageSizes={[3, 6, 9]}
                  onChange={e => this.handlePageChange(e)}
                />
              </TableContainer>
            );
          }}
        />
      </div>
    );
  }
}

export default HMCTable;
